#Extract all SpCas9 recognition sequences from the given BAM
function Extract () { \
	cd $Dir && \
	pv -c $1 | samtools view -@ 8 | awk '{print ($10)}' | awk '{for(i=1; i<=128; i++) {print substr($0, i, 23)}}' | tee >( rg .{21}GG > $2.pos.Cas9.txt ) | rg CC.{21} | rev | tr ACGT TGCA > $2.neg.Cas9.txt && \
	cat $2.pos.Cas9.txt $2.neg.Cas9.txt | LANG=C sort > $2.Cas9.txt && rm $2.pos.Cas9.txt $2.neg.Cas9.txt && \
	#Subtract the sequence of one-time hit from the total sequence
	pv -c  $2.Cas9.txt | uniq -c | rg -w 1 | awk '{print $2"\t"$1}' > $2.Cas9.basecallerror.txt && \
	join -v 1 $2.Cas9.txt $2.Cas9.basecallerror.txt > $2.Cas9.targets.1.txt && rm $2.Cas9.basecallerror.txt $2.Cas9.txt && \
	#Convert PAM to NGG, sort, and uniq -c
	pv -c $2.Cas9.targets.1.txt  | awk '{print substr($1,1,20)".GG"}' | LANG=C sort | uniq -c | awk '{print $2"\t"$1}' > $2.Cas9.targets.2.txt && rm $2.Cas9.targets.1.txt }

#Preparation for extraction of allele-specific Cas9 recognition sequences
function AlleleSpecific () { \
	cd $Dir && \
	join T21.Cas9.targets.2.txt dP.Cas9.targets.2.txt > T21-dP_joined.txt & \
	join T21.Cas9.targets.2.txt dM1.Cas9.targets.2.txt > T21-dM1_joined.txt & wait && \
	join T21-dP_joined.txt dM1.Cas9.targets.2.txt > T21-dP-dM1_joined.txt & \
	join T21-dP_joined.txt dM2.Cas9.targets.2.txt > T21-dP-dM2_joined.txt & \
	join T21-dM1_joined.txt  dM2.Cas9.targets.2.txt > T21-dM1-dM2_joined.txt & wait && \
	#Extraction of M2-allele-specific Cas9 recognition sequences
	join -v 1 T21-dP-dM1_joined.txt dM2.Cas9.targets.2.txt  | rg -v TTTT | rg -v N | rg -w -v 2 | rg -w -v 3 | rg -w -v 4 > M2.specific.Cas9_1.txt & \
	#Extraction of M1-allele-specific Cas9 recognition sequences
	join -v 1 T21-dP-dM2_joined.txt dM1.Cas9.targets.2.txt  | rg -v TTTT | rg -v N | rg -w -v 2 | rg -w -v 3 | rg -w -v 4 > M1.specific.Cas9_1.txt & \
	#Extraction of M1-allele-specific Cas9 recognition sequences
	join -v 1 T21-dM1-dM2_joined.txt dP.Cas9.targets.2.txt | rg -v TTTT | rg -v N | rg -w -v 2 | rg -w -v 3 | rg -w -v 4 > P.specific.Cas9_1.txt  & wait && \
	#Erase the intermediate files
	rm T21-dP_joined.txt T21-dP-dM1_joined.txt T21-dP-dM2_joined.txt T21-dM1_joined.txt T21-dM1-dM2_joined.txt }

