	cd $Dir ;  for q in {1..$(cat $1.specific.Cas9_3.txt | wc -l | awk '{print $1}' )} ; do sed -n ${q}p $1.specific.Cas9_3.txt | awk 'BEGIN{m=100000}{if(m>$2) m=$2}{if(m>$3) m=$3}{if(m>$4) m=$4} END{printf $0"\t"m"\t\n"}' >> $1.specific.Cas9_4.txt ; done }

